mapper.insert:
  		INSERT INTO CONCERT (
		   						CONCERT_ID
		   						, ARTIST
		   						, LOCATION
		   						, CONCERT_DAY
		   						, ADVANCE_BOOKING_DAY
		   						, ADVANCE_BOOKING_URL
		   					 ) 
		VALUES ( 
					'CC-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(CONCERT_ID_SEQ, 6, '0')
					, #{artist}
					, #{location}
					, #{concertDay}
					, #{advanceBookingDay}
					, #{advanceBookingURL}
		  		)
