mapper.insert:
  		INSERT	INTO PREFER (
							   PREFER_ID
							   , EMAIL
							   , CONCERT_ID
							   ) 
		VALUES (
				'PR-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(PREFER_ID_SEQ.NEXTVAL, 6, '0') 
				, #{email}
				, #{concertId}
				)

mapper.select:
SELECT * FROM ( SELECT B.* , ROWNUM RNUM FROM ( SELECT P.PREFER_ID preferId , P.EMAIL email , P.CONCERT_ID concertId , C.SUBJECT , C.CONTENTS , C.C_concertId , C.advanceBookingUrl , C.advanceBookingDay , M.EMAIL M_EMAIL , M.NAME , M.LOGIN_FAIL_COUNT , M.LATEST_LOGIN , M.AUTHORITY FROM PREFER P , MEMBERS M , ( SELECT SUBJECT , CONTENTS , CONCERT_ID C_concertId , ADVANCE_BOOKING_URL advanceBookingUrl , TO_DATE(ADVANCE_BOOKING_DAY,'YYYY/MM/DD/HH24') advanceBookingDay FROM CONCERT WHERE ADVANCE_BOOKING_DAY NOT LIKE '티켓오픈일추후공지' ) C WHERE P.EMAIL = M.EMAIL AND P.CONCERT_ID = C.C_concertId AND C.advanceBookingDay >= SYSDATE AND M.EMAIL = #{email}
AND C.SUBJECT LIKE '%' || '${searchKeyword}' || '%'
ORDER BY C.C_concertId DESC ) B WHERE ROWNUM <= #{endRow} ) WHERE RNUM >= #{startRow}
