mapper.insert:
	   INSERT	INTO CONCERT_REPLY (
	   							REPLY_ID
	   							, CONCERT_ID
	   							, CONTENT
	   							, CRT_DATE
	   							, EMAIL
   							) 
		VALUES ( 
					 'CR-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(CONCERT_REPLY_ID_SEQ.NEXTVAL, 6, '0')
					 , #{concertId}
					 , #{content}
					 , SYSDATE
					 , #{email} 
				)

mapper.update:
  		UPDATE	CONCERT_REPLY
		SET  	CONTENT = #{content}
		WHERE	REPLY_ID = #{replyId}
