mapper.select:
SELECT * FROM ( SELECT B.* , ROWNUM RNUM FROM ( SELECT Q.ID , Q.SUBJECT , Q.CONTENT , Q.CRT_DATE , Q.MDF_DATE , Q.FILE_NAME , Q.ORIGIN_FILE_NAME , Q.EMAIL Q_EMAIL , M.EMAIL M_EMAIL , M.NAME , M.LOGIN_FAIL_COUNT , M.LATEST_LOGIN , M.AUTHORITY FROM QNA Q , MEMBERS M WHERE Q.EMAIL = M.EMAIL
AND SUBJECT LIKE '%' || '${searchKeyword}' || '%'
ORDER BY ID DESC ) B WHERE ROWNUM <= #{endRow} ) WHERE RNUM >= #{startRow}

mapper.update:
UPDATE	QNA
SET		SUBJECT = #{subject}
		, CONTENT = #{content}
		, MDF_DATE = SYSDATE
WHERE	ID = #{id}
