mapper.insert:
	   INSERT	INTO REPLY (
	   							REPLY_ID
	   							, QNA_ID
	   							, CONTENT
	   							, CRT_DATE
	   							, EMAIL
   							) 
		VALUES ( 
					 'RP-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(REPLY_ID_SEQ.NEXTVAL, 6, '0')
					 , #{qnaId}
					 , #{content}
					 , SYSDATE
					 , #{email} 
				)

mapper.update:
  		UPDATE	REPLY
		SET  	CONTENT = #{content}
		WHERE	REPLY_ID = #{replyId}
